oFrases["oferta_31"].kg = { text: "3.1.	КЫЗМАТТАРДЫН наркын Аткаруучу же Аткаруучу ыйгарым укугун берген жак Төлөөчү киргизген төлөмдүн суммасына карата пайыздык катышта жана/же ар бир [бир] төлөм үчүн бекитилген суммада жана/же чектелген [эң чоң жана/же эң аз] маанилерин эсепке алуу менен наркын аныктоонун бир нече жолун айкаштырууну пайдалануу менен аныктайт жана бул нарк төлөм жүргүзүлгөн убакытка, төлөмдүн суммасына, ошондой эле төлөм жүргүзүүнүн ар бир Кабыл алуучу өзүнчө аныктай ала турган башка шарттарына жараша өзгөрүшү мүмкүн. Мында Аткаруучу Төлөөчүнү КЫЗМАТТАРДЫН наркы жөнүндө төлөм жүргүзөрдөн мурда маалымдар кылат. Төлөөчү акча каражаттарын ушул Өзүн-өзү тейлөө автоматынын купюра кабыл алгычына салганга [киргизгенге] чейин КЫЗМАТТАРДАН каалаган учурда баш тарта алат." };
oFrases["oferta_32"].kg = { text: "3.2.	Кызматтардын акысын төлөө Төлөөчү ушул Өзүн-өзү тейлөө автоматы аркылуу киргизген тиешелүү төлөмдүн суммасынан КЫЗМАТТАРДЫН наркын Аткаруучунун же Аткаруучу ыйгарым укугун берген жактын кармап алышы аркылуу жүргүзүлөт." };
oFrases["oferta_33"].kg = { text: "3.3.	КЫЗМАТТАРДЫ көрсөтүү жана алардын акысын төлөө факты ушул Өзүн-өзү тейлөө автоматы берген тиешелүү документ [квитанция, касса чеги же ушул сыяктуу башка документ] менен, ошондой эле башка жол менен, анын ичинде Аткаруучунун баяндама маалыматтарынын негизинде тастыкталат." };
oFrases["oferta_34"].kg = { text: "4.	ӨЗГӨЧӨ ШАРТТАР" };
