oFrases["info_t8"].ru_ru = { text: "Обязательства плательщика перед провайдером считаются исполненными с момента внесения им наличных денежных средств в терминал." };
oFrases["info_t9"].ru_ru = { text: "Обязательно сохраняйте квитанцию, являющуюся подтверждением совершения платежа." };
